public class Ex14c8 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int colunas = 5, linhas = 5;

		for(int aux = 0; aux < linhas; aux++){
		    for(int auxi = 0; auxi < colunas; auxi++){
		        System.out.print("*"); // colunas
		    }
		    System.out.println(); // linhas
		}
	}

}
